fn font-render ( c x y -- ) {
  2 # @ 3 << font-blob + 2 # !

  font-sx do
    font-sy do
      2 # @ i + @ 1 j << and
      if
        255 255 255
      else
        0 0 0
      then

      1 # @ j +
      0 # @ i +
      fb-put
    loop
  loop
}

val font-blob
  blob 
  to font-blob

val font-sx
  8 to font-sx

val font-sy
  8 to font-sy
