fn font-render ( c x y -- ) {
  3 # @ $7f > if $3f 3 # ! then

  3 # @ 3 << font-blob + 3 # !

  font-sx do
    font-sy do
      3 # @ i + @ 1 8 j - << and
      if
        255 255 255
      else
        0 0 0
      then

      2 # @ j +
      1 # @ i +
      fb-put
    loop
  loop

  3 leave
}

val font-blob
  blob 
  to font-blob

val font-sx
  8 to font-sx

val font-sy
  8 to font-sy
